const urlString="https://www.metaweather.com/api/location/search/?query=",urlID="https://www.metaweather.com/api/location/";let inputName=document.getElementById("name"),inputId=document.getElementById("id"),btn=document.getElementById("btn"),container=document.querySelector(".container");function axiosUrlString(){axios.get(urlString+inputName.value).then(e=>{e=e.data,container.innerHTML=e[0].title+", "+e[0].location_type+", "+e[0].woeid+", "+e[0].latt_long}).catch(e=>{console.log("Error fetching and parsing data.",e)})}function axiosUrlId(){axios.get(urlID+inputId.value).then(e=>{responseD=e.data.consolidated_weather[0],container.innerText=responseD.id+", "+responseD.weather_state_name+", "+responseD.wind_direction_compass+", "+responseD.applicable_date+", "+responseD.min_temp+", "+responseD.max_temp+", "+responseD.the_temp}).catch(e=>{console.log("Error fetching and parsing data.",e)})}inputId.addEventListener("keyup",()=>(""==inputId.value||null==inputId?btn.innerText="Get Location":btn.innerText="Get Forecast",btn.innerText)),btn.addEventListener("click",e=>{e.preventDefault(),""==inputId.value&&""===inputName.value?container.innerText="":"Get Location"==btn.innerText?axiosUrlString():axiosUrlId()});